mapper.update:
UPDATE NOTICEBOARDS 
SET NBSTATE=${nbstate}
WHERE NBCODE=#{nbcode}

UPDATE USEDBOARDS 
SET UBSTATE=${ubstate}
WHERE UBCODE=#{ubcode}

UPDATE BOARDS 
SET BDSTATE=${bdstate}
WHERE BDCODE=#{bdcode}

mapper.select:
SELECT RPBDCATEGORY, RPCODE, RPBDCODE, RPCONTENTS, RPMID, TO_CHAR(RPDATE,'YY-MM-DD HH24:MI') AS RPDATE, RPSTATE, RPNICKNAME
FROM
(SELECT ROWNUM RN, RPBDCATEGORY, RPCODE, RPBDCODE, RPCONTENTS, RPMID, RPDATE, RPSTATE, RPNICKNAME
FROM ADMIN_RPLIST_VIEW

	WHERE RPBDCATEGORY=#{searchVal}



		AND


		WHERE



	${searchType} LIKE '%'||#{keyword}||'%'

)
WHERE RN BETWEEN ${startRow} AND ${endRow}

mapper.update:
UPDATE REPLY 
SET RPSTATE=${rpstate}
WHERE RPCODE=#{rpcode}

mapper.select:
SELECT CTCODE, CTTITLE, CTCONTENTS, CTMID, CTNICKNAME, TO_CHAR(CTDATE,'YY-MM-DD HH24:MI') CTDATE, CTANS, CTANSDATE FROM (SELECT ROWNUM RN, ADMIN_CTLIST_VIEW.* FROM ADMIN_CTLIST_VIEW
WHERE CTANS IS NULL
WHERE CTANS IS NOT NULL
) WHERE RN BETWEEN ${startRow} AND ${endRow}
