mapper.update:

UPDATE REPLY 
SET RPSTATE = 0
WHERE RPCODE = #{rpcode}

mapper.select:

SELECT RCBDCODE
FROM RECOMMEND
WHERE RCMID = #{loginId}
AND RCBDCODE = #{bdcode}


SELECT WBEDBDCODE
FROM WARNINGBOARDS
WHERE WBMID = #{loginId}
AND WBEDBDCODE = #{bdcode}

mapper.delete:

DELETE FROM WARNINGBOARDS
WHERE WBMID = #{loginId}
AND WBEDBDCODE = #{bdcode}

mapper.select:

 		SELECT COUNT(*)
		FROM RECOMMEND
		WHERE RCBDCODE = #{bdcode}

mapper.update:

UPDATE BOARDS
SET BDSTATE = 0
WHERE BDCODE = #{bdcode}


UPDATE BOARDS
SET BDCATEGORY = #{bdcategory},
BDRGCODE = #{bdrgcode},
BDTITLE = #{bdtitle},
BDCONTENTS = #{bdcontents}
WHERE BDCODE = #{bdcode}


UPDATE Boards
SET bdhits = bdhits +1
WHERE bdCODE = #{bdcode}

mapper.select:
select count(*) from 


	recommend where rcbdcode = #{bdcode} 
	and rcmid=#{loginId}


	scrap where scbdcode = #{bdcode}
	and scmid=#{loginId}


	warningboards where wbedbdcode=#{bdcode}
	and wbmid=#{loginId}

mapper.delete:
delete from 

	recommend where rcbdcode = #{bdcode} 
	and rcmid=#{loginId}


	scrap where scbdcode = #{bdcode}
	and scmid=#{loginId}


	warningboards where wbedbdcode=#{bdcode}
	and wbmid=#{loginId}

mapper.update:

UPDATE BOARDS
SET BDHITS = BDHITS + 1
WHERE BDCODE = #{bdcode}
