mapper.select:
SELECT * FROM ( select ROWNUM RN, VIEW_LATESTNOTICELIST.* from VIEW_LATESTNOTICELIST
WHERE
(NBTITLE LIKE '%'||#{keyword}||'%' OR NBCONTENTS LIKE '%'||#{keyword}||'%')
${searchType} LIKE '%'||#{keyword}||'%'
) WHERE RN BETWEEN ${startRow} AND ${endRow}

mapper.update:

UPDATE REPLY 
SET RPSTATE = 2
WHERE RPCODE = #{rpcode}

mapper.select:

SELECT RCBDCODE
FROM RECOMMEND
WHERE RCMID = #{loginId}
AND RCBDCODE = #{bdcode}


SELECT WBEDBDCODE
FROM WARNINGBOARDS
WHERE WBMID = #{loginId}
AND WBEDBDCODE = #{bdcode}

mapper.delete:

DELETE FROM WARNINGBOARDS
WHERE WBMID = #{loginId}
AND WBEDBDCODE = #{bdcode}

mapper.select:

 		SELECT COUNT(*)
		FROM RECOMMEND
		WHERE RCBDCODE = #{bdcode}

mapper.update:

UPDATE BOARDS
SET BDSTATE = 2
WHERE BDCODE = #{bdcode}


UPDATE Boards
SET bdhits = bdhits +1
WHERE bdCODE = #{bdcode}

mapper.select:
select count(*) from 


	recommend where rcbdcode = #{bdcode} 
	and rcmid=#{loginId}


	scrap where scbdcode = #{bdcode}
	and scmid=#{loginId}


	warningboards where wbedbdcode=#{bdcode}
	and wbmid=#{loginId}

mapper.delete:
delete from 

	recommend where rcbdcode = #{bdcode} 
	and rcmid=#{loginId}


	scrap where scbdcode = #{bdcode}
	and scmid=#{loginId}


	warningboards where wbedbdcode=#{bdcode}
	and wbmid=#{loginId}

mapper.update:

UPDATE BOARDS
SET BDHITS = BDHITS + 1
WHERE BDCODE = #{bdcode}


UPDATE NOTICEBOARDS
SET NBHITS = NBHITS + 1
WHERE NBCODE = #{nbcode}

mapper.select:

SELECT RPCODE, RPBDCODE, RPCONTENTS, RPMID, RPDATE, RPSTATE
FROM REPLY
WHERE RPCODE = #{rpcode}

mapper.update:

UPDATE REPLY
SET RPCONTENTS = #{rpcontents} 
WHERE RPCODE = #{rpcode}


UPDATE BOARDS
SET BDTITLE = #{bdtitle},
BDCONTENTS = #{bdcontents},
BDIMG = #{bdimg},
BDDETAILIMG = #{bddetailimg}
WHERE BDCODE = #{bdcode}

