mapper.update:
UPDATE GONGUBOARDS 
SET GBSTATE=${gbstate}
WHERE GBCODE=#{gbcode}

UPDATE GONGUBOARDS 
SET GBFIX=${gbfix}
WHERE GBCODE=#{gbcode}

mapper.select:
SELECT * FROM ( select ROWNUM RN, VIEW_LATESTGONGULIST.* from VIEW_LATESTGONGULIST
WHERE
(GBTITLE LIKE '%'||#{keyword}||'%' OR GBCONTENTS LIKE '%'||#{keyword}||'%')
${searchType} LIKE '%'||#{keyword}||'%'
) WHERE RN BETWEEN ${startRow} AND ${endRow}

mapper.update:
UPDATE GONGUBOARDS
SET GBHITS = GBHITS + 1
WHERE GBCODE = #{gbcode}
