mapper.select:
SELECT MNICKNAME
FROM MEMBERS
WHERE MID = #{loginId}

SELECT ZZMID
FROM ZZIM
WHERE ZZMID =#{loginId} AND ZZUBCODE= #{ubcode}

SELECT UBCODE, UBSELLBUY, UBTITLE, TO_CHAR(UBDATE, 'YYYY-MM-DD HH24:MI') AS UBDATE, UBMAINIMG
FROM USEDBOARDS
WHERE UBMID = #{ubmid}


SELECT WUEDUBCODE
FROM WARNINGUSEDBOARDS
WHERE WUMID = #{loginId}
AND WUEDUBCODE = #{ubcode}

mapper.delete:

DELETE FROM WARNINGUSEDBOARDS
WHERE WUMID = #{loginId}
AND WUEDUBCODE = #{ubcode}

mapper.update:
UPDATE USEDBOARDS
SET UBSTATE = 2
WHERE UBCODE = #{ubcode}

UPDATE GOODS
SET GDSTATE = 2
WHERE GDUBCODE = #{ubcode}

UPDATE USEDBOARDS
SET UBSTATE = #{ubstate} 	
WHERE UBCODE = #{ubcode}

UPDATE GOODS
SET GDSTATE = #{gdstate}
WHERE GDCODE = #{gdcode}
