mapper.select:
SELECT MNICKNAME
FROM MEMBERS
WHERE MID = #{loginId}

SELECT UBCODE, UBRGCODE, UBSELLBUY, UBMID, UBTITLE, UBCONTENTS, UBDATE, UBMAINIMG, UBDETAILIMG, UBSTATE, UBZZIM, UBMSTATE, UBPROFILE, UBNICKNAME
FROM (SELECT ROWNUM RN, PAGESELECT_VIEW.* FROM PAGESELECT_VIEW WHERE UBSELLBUY = #{paging.sellBuy}
AND UBRGCODE = #{paging.searchVal}
AND (UBTITLE LIKE '%'||#{paging.keyword}||'%' OR UBCONTENTS LIKE '%'||#{paging.keyword}||'%')
AND UBMID IN (SELECT MID FROM MEMBERS WHERE MNICKNAME LIKE '%'||#{paging.keyword}||'%')
AND UBTITLE LIKE '%'||#{paging.keyword}||'%'
AND UBCONTENTS LIKE '%'||#{paging.keyword}||'%'
)
WHERE RN BETWEEN 1 AND 3
WHERE RN BETWEEN #{paging.startRow} AND #{paging.endRow}

SELECT COUNT(UBCODE) FROM PAGESELECT_VIEW WHERE UBSELLBUY = #{sellBuy}
AND UBRGCODE = #{searchVal}
AND (UBTITLE LIKE '%'||#{keyword}||'%' OR UBCONTENTS LIKE '%'||#{keyword}||'%')
AND UBMID IN (SELECT MID FROM MEMBERS WHERE MNICKNAME LIKE '%'||#{keyword}||'%')
AND UBTITLE LIKE '%'||#{keyword}||'%'
AND UBCONTENTS LIKE '%'||#{keyword}||'%'

SELECT GDCODE, GDUBCODE, GDNAME, GDPRICE, GDSTATE
		FROM GOODS GD, USEDBOARDS UB
		WHERE GDUBCODE = UBCODE AND UBCODE = #{ubcode}
		AND UBSELLBUY = #{ubsellbuy}
		ORDER BY GDCODE

SELECT UBCODE, UBRGCODE, UBSELLBUY, UBMID, UBTITLE, UBCONTENTS, UBDATE, UBMAINIMG, UBDETAILIMG, UBSTATE, UBZZIM, UBMSTATE, UBPROFILE, UBNICKNAME
FROM (SELECT ROWNUM RN, PAGESELECT_VIEW.* FROM PAGESELECT_VIEW WHERE UBMID IN (SELECT MID FROM MEMBERS WHERE MNICKNAME LIKE '%'||#{keyword}||'%' ) )
WHERE RN BETWEEN #{startRow} AND #{endRow}

SELECT ZZMID
		FROM ZZIM
		WHERE ZZMID =#{loginId} AND ZZUBCODE= #{ubcode}


SELECT WUEDUBCODE
		FROM WARNINGUSEDBOARDS
		WHERE WUMID = #{loginId}
		AND	WUEDUBCODE = #{ubcode}

mapper.delete:
DELETE FROM ZZIM
WHERE ZZMID = #{zzmid} AND ZZUBCODE =
#{zzubcode}


DELETE FROM WARNINGUSEDBOARDS
WHERE WUMID = #{loginId}
AND WUEDUBCODE =
#{ubcode}

mapper.update:
UPDATE GOODS
SET GDSTATE = 2
WHERE GDUBCODE = #{ubcode}

UPDATE GOODS
SET GDSTATE = #{gdstate}, GDPRICE =
#{gdprice},
GDNAME = #{gdname}
WHERE GDCODE =
#{gdcode}

UPDATE USEDBOARDS
SET UBSTATE = #{ubstate}
WHERE UBCODE
= #{ubcode}

UPDATE GOODS
SET GDSTATE = #{gdstate}
WHERE GDCODE =
#{gdcode}
